<?php
$response = null;

if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    // base64 ที่คุณให้มา
    $imageBase64 = '';
    $deviceId = 'B002';

    $ch = curl_init();
    curl_setopt_array($ch, [
        CURLOPT_URL => 'https://landslide-alerts.com/api/IoT/environment',
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_POST => true,
        // ส่งแบบ form-data
        CURLOPT_POSTFIELDS => [
            'image' => $imageBase64,
            'device_id' => $deviceId,
        ],
    ]);
    $res = curl_exec($ch);
    $err = curl_error($ch);
    curl_close($ch);

    if ($err) {
        $response = json_encode([
            'success' => false,
            'error' => $err,
        ], JSON_PRETTY_PRINT | JSON_UNESCAPED_UNICODE);
    } else {
        $response = $res;
    }
}
?>
